function ProductItem(props){
    const {name, price, count, id, removeProduct, plusNum, minusNum} = props
    onst [doubleClick, setDoubleClick] = useState(false);
    return(
        <div className="todo_product"
        onDoubleClick={() => removeProduct(id)}
        
        
        >
            <h2>{name}</h2>
            <p>{price}</p>
            <button
  onClick={() => {
    if (!doubleClick) {
      minusNum(count - 1, id);
    }
  }}
>
  -
</button>
{count}
<button
  onClick={() => {
    if (!doubleClick) {
      plusNum(count + 1, id);
    }
  }}
>
  +
</button>
        </div>
    )
}

export default ProductItem
