import { useState } from "react"

function ProductItem(props){
    const {name, price, count, id, revoveProduct, data} = props
    const {setItem} = useState(data)
    return(
        <div className="todo_product"
        onDoubleClick={() => revoveProduct(id)}
        
        >
            <h2>{name}</h2>
            <p>{price}</p>
            <button>-</button>
            {count}
            <button onClick={() => setItem(count + 1)}></button>
        </div>
    )
}

export default ProductItem
